import React from "react";
import * as Actions from "../../Actions";
import { connect } from "react-redux";
import VerifyOtp from "../../Components/Mobile/verifyOtp";

class CheckOtp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      otp : "",
      contact: "",
      respMessage: '',
      respSuccess: false,
      onClick: false,
      verify : false

    }
    this.handleVerifyOnSubmit = this.handleVerifyOnSubmit.bind(this)
  }

  componentWillReceiveProps(nextProps) {
    if (
      nextProps.sentOtp &&
      nextProps.sentOtp.error &&
      nextProps.sentOtp.error.data &&
      !nextProps.sentOtp.error.data.success
    ) {
      this.setState({
        respMessage: nextProps.sentOtp.error.data.message
          ? nextProps.sentOtp.error.data.message
          : nextProps.sentOtp.error.data.error.message,
        respSuccess: true
      })
    } else {
      if (
       ( nextProps.sentOtp &&
        nextProps.sentOtp.data.data &&
        nextProps.sentOtp.data.data.success)
      ) {
        this.setState({
          respMessage: nextProps.sentOtp.data.data.message,
          respSuccess: true,
          verify : true,
          contact : nextProps.sentOtp.data.data.contact
        })

      } else {
        this.setState({
          respMessage: nextProps.sentOtp.data.message,
          respSuccess: true
        })
      }
    }
  }

  handleVerifyOnSubmit = (values, actions) => {
    const { otp } = values
    const { contact } = localStorage.getItem("contact");
    const { dispatch } = this.props
    dispatch(Actions.verifyOtp({ contact, otp }))
  }

  handleClose = () => {
    this.setState({
      respSuccess: false
    })
  }

  render() {
    const { contact
      , respSuccess, respMessage, otp } = this.state;
    let  initialValues = { otp } 
    const { handleClose, handleVerifyOnSubmit } = this
    return (
      <VerifyOtp 
        contact              = { contact }
        initialValues        = { initialValues }
        handleVerifyOnSubmit = { handleVerifyOnSubmit }
        respMessage          = { respMessage }
        respSuccess          = { respSuccess }
        handleClose          = { handleClose }
      /> 
    )
  }
}


const mapState = (state) => {
  return {
    verifyOtp: state.verifyOtpResp
  }
}

export default connect(mapState)(CheckOtp)
